function RavenMother(a){Entity.call(this);this.name="RavenMother";this.maxHp.base=3E3;this.maxSp.base=500;this.maxLust.base=500;this.strength.base=100;this.stamina.base=120;this.dexterity.base=150;this.intelligence.base=90;this.spirit.base=100;this.libido.base=100;this.charisma.base=80;this.level=20;this.sexlevel=15;this.combatExp=800;this.coinDrop=1500;this.body.DefMale();this.Butt().buttSize.base=2;this.body.SetRace(Race.avian);TF.SetAppendage(this.Back(),AppendageType.wing,Race.avian,Color.black);
this.body.SetBodyColor(Color.white);this.body.SetEyeColor(Color.green);this.SetLevelBonus();this.RestFull();this.flags.Stage=0;this.flags.Met=0;this.flags.KeptRaven=0;this.flags.RBlock=0;a&&this.FromStorage(a)}RavenMother.prototype=new Entity;RavenMother.prototype.constructor=RavenMother;RavenMother.Stage={ravenstage2:8,ravenstage3:12};RavenMother.prototype.Ravenness=function(){return Math.floor(this.flags.Stage/100)};
